import React, { Component } from 'react'
import Auxiliary from '../../Hoc/Auxiliary' 
import Burger from '../../components/Burger/Burger'
import BurgerControl from '../../components/Burger/BurgerControl/BurgerControl'
const INGREDIENTS_PRICES ={
    salad: 0.5,
    cheese: 0.4,
    bacon:1.0,
    meat:1.5,
}

class BurgerBuilder extends Component {
    //  constructor(props){
    //     super(props);
    //     this.state={...}

    //  }

     state={
         items:{
             salad:0,
             bacon:0,
             cheese:0,
             meat:0,
         },
         totalPrice:4

     }

     addIngredientHandler = (type)=>{
                const oldCount = this.state.items[type];
                const updatedCount = oldCount +1;
                const updatedIngredients = {
                    ...this.state.items
                }
                updatedIngredients[type]=updatedCount;
                const priceAddition = INGREDIENTS_PRICES[type];
                const newPrice = this.state.totalPrice + priceAddition;
                this.setState({ totalPrice:newPrice,items:updatedIngredients})
     }

     removeIngredientHandler =(type)=>{
                if(this.state.items[type]!==0){
        const oldCount = this.state.items[type];
        const updatedCount = oldCount -1;
        const updatedIngredients = {
            ...this.state.items
        }
        updatedIngredients[type]=updatedCount;
        const priceAddition = INGREDIENTS_PRICES[type];
        const newPrice = this.state.totalPrice - priceAddition;
        this.setState({ totalPrice:newPrice,items:updatedIngredients})
     }
    }
    render() {

        const disabledInfo={
            ...this.state.items
        };
        for(let key in disabledInfo){}
        return (
            <Auxiliary>
                {/* <div>Burger</div> */}
                <Burger items={this.state.items}/>
                {/* <div>Build Controls</div> */}
                <BurgerControl 
                    ingredientAdded={this.addIngredientHandler}
                    ingredientRemoved ={this.removeIngredientHandler}
                />
            </Auxiliary>
                
            
        )
    }
}

export default BurgerBuilder
